mapper.insert:
insert
  into free_board 
  (
    free_no
  , free_title
  , mem_no
  , free_cnt 
  , free_file_origin
  , free_file_update 
  )
  values
  ( 
    seq_frno.nextval 
  , #{freeTitle}
  , #{memNo}
  , #{freeCnt}   		  
  , #{freeFileOrigin}
  , #{freeFileUpdate} 
  )

mapper.update:
update
	   free_board
   set free_title = #{freeTitle} 
     , free_cnt = #{freeCnt} 
     , free_file_origin = #{freeFileOrigin} 
     , free_file_update = #{freeFileUpdate} 
 where free_no = #{freeNo}
