mapper.select:
update  
       member
   set dept_no = #{deptNo}
     , posi_no = #{posiNo}
     , hire_date = to_char(to_date(#{hireDate}, 'YYYY-MM-DD'), 'MM/DD/YYYY')
     , account = #{account}
     , regi = #{regi}
     , accept_status = 'Y'
 where mem_no = #{memNo}

mapper.update:
		update
			   member
		   set
               profile = #{profile}
             , mem_name = #{memName}
             , dept_name = #{deptName}
             , posi_name = #{posiName}
		     , phone = #{phone}
		     , to_char(to_date(hire_date, 'MM/DD/YYYY'), 'YYYY-MM-DD') hire_date = #{hireDate}
             , account = #{account}
             , regi = #{regi}
             , office_email = #{officeEmail}
             , personal_email = #{personalEmail}
             , post = #{post}
             , road_address = #{roadAddress}
             , detail_address= #{detailAddress}
             , mem_status = #{memStatus}
             , admin_status = #{adminStatus}
		  from member m
          join dept d on (m.dept_no = d.dept_no)
          join position p on (m.posi_no = p.posi_no)
		 where mem_no = #{memNo}
